package test;

public class ForTest01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        /*编写一个程序从1循环到150，并在每行打印一个值，
         * 在每个3的倍数行上打印出“foo”，
         * 5的倍数行打印“biz",
         * 7的倍数行打印“baz".
         */
		for (int i = 1;i <= 150;i ++) {
			System.out.print(i + " ");
			if(i % 3 == 0) {
				System.out.print("foo "); 
			}
			if(i % 5 == 0) {
				System.out.println("biz ");
				
			}
			if(i % 7 == 0) {
			    System.out.println("baz ");
			    
		}
	            System.out.println();//换行
		}
 }
}
